const headers = $input.item.json.headers ?? {};
const authHeader = headers['authorization'] ?? headers['Authorization'] ?? '';
const providedKey = authHeader.replace(/^Bearer\s+/i, '').trim();
const expectedKey = $env.DMARC_API_KEY ?? '';

// Constant-time comparison to prevent timing attacks
if (!providedKey || providedKey.length !== expectedKey.length) {
  throw new NodeOperationError($node, 'Unauthorized: missing or invalid API key', { httpCode: '401' });
}
let diff = 0;
for (let i = 0; i < providedKey.length; i++) {
  diff |= providedKey.charCodeAt(i) ^ expectedKey.charCodeAt(i);
}
if (diff !== 0) {
  throw new NodeOperationError($node, 'Unauthorized: invalid API key', { httpCode: '401' });
}

// Pass body through
return { json: $input.item.json.body ?? {} };

const body = $input.item.json;
const domain = (body.domain ?? '').trim().toLowerCase();

if (!domain) {
  throw new NodeOperationError($node, 'Bad Request: domain is required', { httpCode: '400' });
}

// Basic domain syntax check (labels separated by dots, no spaces)
const domainRegex = /^(?:[a-z0-9](?:[a-z0-9-]{0,61}[a-z0-9])?\.)+[a-z]{2,}$/;
if (!domainRegex.test(domain)) {
  throw new NodeOperationError($node, `Bad Request: invalid domain format "${domain}"`, { httpCode: '400' });
}

const options = body.options ?? {};

return {
  json: {
    request_id:    crypto.randomUUID(),
    domain,
    dkim_selector: options.dkim_selector ?? null,
    callback_url:  options.callback_url  ?? null,
    requested_at:  new Date().toISOString()
  }
};

const ctx    = $('Validate Input').item.json;
const result = $input.item.json;

return {
  json: {
    request_id:  ctx.request_id,
    domain:      ctx.domain,
    timestamp:   new Date().toISOString(),
    risk_level:  result.risk_level,
    analysis:    result,
    callback_url: ctx.callback_url
  }
};
